local a=vim.fn;local b={}function b.is_inside_fold(c)return a.foldclosed(c+1)~=-1 end;function b.is_in_insert_mode()local d=vim.api.nvim_get_mode().mode;return d=='i'or d=='R'end;function b.str_contains(e,f)return e:find(f,1,true)~=nil end;function b.str_contains_any(e,g)for h,f in ipairs(g)do if b.str_contains(e,f)then return true end end;return false end;function b.get_cursor_pos()local c,i=unpack(vim.api.nvim_win_get_cursor(0))return c-1,i end;function b.find_forward(j,f,k)local l,h,m=string.find(j,f,k and k+1)return l,m end;function b.find_backward(n,f,k)local o=#n+1;local l,m=b.find_forward(n,f,k and o-k)if l then return o-l,m end end;function b.get_lines(p,q)return vim.api.nvim_buf_get_lines(0,p,p+q,false)end;return b
