return{cmd='actionlint',stdin=true,args={'-format','{{json .}}'},ignore_exitcode=true,parser=function(a,b)if a==''then return{}end;local c=vim.json.decode(a)if c==nil then return{}end;local d={}for e,f in ipairs(c)do local g=vim.api.nvim_buf_get_name(b)local h=vim.fn.getcwd()..'/'..f.filepath;if g==h then table.insert(d,{lnum=f.line-1,end_lnum=f.line-1,col=f.column-1,end_col=f.end_column,severity=vim.diagnostic.severity.WARN,source='actionlint: '..f.kind,message=f.message})end end;return d end}
