return{cmd='buf',args={'lint','--error-format','json'},stdin=false,ignore_exitcode=true,parser=function(a)if a==''then return{}end;local b=vim.split(a,'\n')local c={}for d,e in ipairs(b)do if e==''then break end;local f=vim.json.decode(e)if f then table.insert(c,{lnum=(f.start_line or 1)-1,col=(f.start_column or 1)-1,end_lnum=(f.end_line or 1)-1,end_col=(f.end_column or 1)-1,severity=vim.diagnostic.severity.WARN,source=f.type,message=f.message})end end;return c end}
