return{cmd='ktlint',stdin=true,args={'--android','--reporter=json','--stdin'},stream='stderr',ignore_exitcode=true,parser=function(a)local b=vim.json.decode(a)if vim.tbl_isempty(b)then return{}end;local c={}for d,e in pairs(b[1].errors)do table.insert(c,{lnum=e.line-1,col=e.column-1,end_lnum=e.line-1,end_col=e.column-1,message=e.message,severity=vim.diagnostic.severity.WARN,source='ktlint'})end;return c end}
