return function()local a=vim.api.nvim_buf_get_name(0)return{cmd='perlimports',stdin=true,args={'--lint','--json','--read-stdin','--filename',a},stream='stderr',parser=function(b)local c=vim.fn.split(b,'\n')local d={}for e,f in ipairs(c)do local g=vim.json.decode(f)table.insert(d,{lnum=g.location.start.line-1,col=g.location.start.column-1,end_lnum=g.location['end'].line-1,end_col=g.location['end'].column-1,severity=vim.diagnostic.severity.INFO,message=g.reason})end;return d end}end
