local a={error=vim.diagnostic.severity.ERROR,warning=vim.diagnostic.severity.WARN,ignored=vim.diagnostic.severity.INFO}return{cmd='staticcheck',stdin=false,args={'-f','json'},ignore_exitcode=true,parser=function(b,c)local d=vim.fn.split(b,"\n")local e=vim.api.nvim_buf_get_name(c)local f={}for g,h in ipairs(d)do local i=vim.json.decode(h)if i.location.file==e then table.insert(f,{lnum=i.location.line-1,col=i.location.column-1,end_lnum=i["end"]["line"]-1,end_col=i["end"]["column"]-1,code=i.code,user_data={lsp={code=i.code}},severity=assert(a[i.severity],'missing mapping for severity '..i.severity),message=i.message})end end;return f end}
