local a={}local b=require("search-replace.util")local c=require("search-replace.config")a.search_replace=function(d)local e=0;if string.len(d)==0 then e=2 else e=1 end;local f=string.rep("\\<Left>",string.len(c.options["default_replace_multi_buffer_options"])+e)vim.cmd(':call feedkeys(":bufdo %s/'..b.double_escape(d).."//"..c.options["default_replace_multi_buffer_options"]..f..'")')end;a.visual_charwise_selection=function()local g=b.get_visual_selection()if g==nil then print("search-replace does not support visual-blockwise selections")return end;local h=string.rep("\\<backspace>",5)local f=string.rep("\\<Left>",string.len(c.options["default_replace_multi_buffer_options"])+1)vim.cmd(':call feedkeys(":'..h.."%s/"..b.double_escape(g).."//"..c.options["default_replace_multi_buffer_options"]..f..'")')end;a.open=function()a.search_replace("")end;a.cword=function()a.search_replace(vim.fn.expand("<cword>"))end;a.cWORD=function()a.search_replace(vim.fn.expand("<cWORD>"))end;a.cexpr=function()a.search_replace(vim.fn.expand("<cexpr>"))end;a.cfile=function()a.search_replace(vim.fn.expand("<cfile>"))end;return a
