local a={}a.name="marks"a.actions={{trigger="`",mode="n"},{trigger="'",mode="n"},{trigger="g`",mode="n"},{trigger="g'",mode="n"}}function a.setup(b,c,d)end;local e={["^"]="Last position of cursor in insert mode",["."]="Last change in current buffer",['"']="Last exited current buffer",["0"]="In last file edited",["'"]="Back to line in current buffer where jumped from",["`"]="Back to position in current buffer where jumped from",["["]="To beginning of previously changed or yanked text",["]"]="To end of previously changed or yanked text",["<lt>"]="To beginning of last visual selection",[">"]="To end of last visual selection"}function a.run(f,g,h)local i={}local j={}for k,l in pairs(vim.fn.getmarklist(h))do table.insert(j,l)end;for k,l in pairs(vim.fn.getmarklist())do table.insert(j,l)end;for k,l in pairs(j)do local m=l.mark:sub(2,2)if m=="<"then m="<lt>"end;local n=l.pos[2]local o;if l.pos[1]and l.pos[1]~=0 then local p=vim.fn.getbufline(l.pos[1],n)if p and p[1]then o=p[1]end end;local q=l.file and vim.fn.fnamemodify(l.file,":p:.")local r=string.format("%4d  ",n)table.insert(i,{key=m,label=e[m]or"",value=r..(o or q or""),highlights={{1,#r-1,"Number"}}})end;return i end;return a
