local a={}function a.insert_toc(b)local c={}if#b.fargs>0 then local d;if vim.startswith(b.args,"/")then d=vim.split(b.args,"/",{plain=true,trimempty=true})else d=b.fargs end;if#d>0 then c.max_level=tonumber(d[1])if c.max_level~=nil then if#d>1 then c.markers={unpack(d,2)}end else c.markers=d end end else c.markers=require("markdown.config"):get().toc.markers end;if c.markers==nil or#c.markers==0 then c.markers={"-"}end;local e,f=require("markdown.util").get_user_command_range(b)c.start_row=e;if b.range~=0 then c.end_row=f end;require("markdown.toc").insert_toc(c)end;local function g(b,h)local c={omit_flagged=h}if#b.fargs>0 then c.max_level=tonumber(b.fargs[1])end;require("markdown.toc").set_loclist_toc(c)end;function a.show_toc(b)g(b,true)end;function a.show_toc_all(b)g(b,false)end;function a.reset_list_numbering(b)local e,f;if b.range~=0 then e,f=require("markdown.util").get_user_command_range(b)else e,f=0,-1 end;require("markdown.list").reset_list_numbering(e,f)end;function a.toggle_task(b)local e,f=require("markdown.util").get_user_command_range(b)require("markdown.list").toggle_task(e,f)end;return a
