local a={["WARN"]=vim.diagnostic.severity.WARN,["FAIL"]=vim.diagnostic.severity.ERROR}return{cmd="cfn_nag_scan",stdin=false,args={"--output-format","json","--input-path"},parser=function(b)if b==nil then return{}end;local c={}local d=vim.json.decode(b)local e=d[1].file_results.violations;for f,g in pairs(e)do for f,h in pairs(g.line_numbers)do table.insert(c,{lnum=h,end_lnum=h,col=1,code=g.id,severity=assert(a[g.type],"missing mapping for severity "..g.type),message=g.message})end end;return c end}
