return{cmd="deno",stdin=false,args={'lint','--json'},ignore_exitcode=true,parser=function(a)local b=vim.json.decode(a)local c={}local d={{name="diagnostics",severity=vim.diagnostic.severity.WARN},{name="errors",severity=vim.diagnostic.severity.ERROR}}for e,f in ipairs(d)do for e,g in ipairs(b[f.name])do if f.name=="errors"then local h,i,j;e,e,h,i,j=string.find(g.message,"([%a%p%s]+):(%d+):(%d+)%s*")g["range"]={start={line=tonumber(i),col=tonumber(j)},["end"]={line=tonumber(i),col=tonumber(j)}}g["message"]=h end;table.insert(c,{source="deno",lnum=g.range.start.line-1,col=g.range.start.col-1,end_lnum=g.range["end"].line-1,end_col=g.range["end"].col,severity=f.severity,message=g.message,code=g.code})end end;return c end}
