local a='([^:]+):(%d+): warning: (.+)'local b={'file','lnum','message'}local c='([^:]+):(%d+): syntax error, (.+)'local d={'file','lnum','message'}local e={require('lint.parser').from_pattern(a,b,nil,{['severity']=vim.diagnostic.severity.WARN,['source']='ruby'}),require('lint.parser').from_pattern(c,d,nil,{['severity']=vim.diagnostic.severity.ERROR,['source']='ruby'})}return{cmd='ruby',stdin=false,args={'-w','-c'},ignore_exitcode=true,stream='stderr',parser=function(f,g)local h={}for i,j in ipairs(e)do local k=j(f,g)for i,l in ipairs(k)do table.insert(h,l)end end;return h end}
