local a={error=vim.diagnostic.severity.ERROR,warning=vim.diagnostic.severity.WARN,info=vim.diagnostic.severity.INFO,style=vim.diagnostic.severity.HINT}return{cmd='shellcheck',stdin=true,args={'--format','json','-'},ignore_exitcode=true,parser=function(b)if b==""then return{}end;local c=vim.json.decode(b)local d={}for e,f in ipairs(c or{})do table.insert(d,{lnum=f.line-1,col=f.column-1,end_lnum=f.endLine-1,end_col=f.endColumn-1,code=f.code,source="shellcheck",user_data={lsp={code=f.code}},severity=assert(a[f.level],'missing mapping for severity '..f.level),message=f.message})end;return d end}
