return{cmd="typos",stdin=true,append_fname=true,args={"--format","json","-"},stream="stdout",ignore_exitcode=true,env=nil,parser=function(a,b)local c={}if a==""then return{}end;for d in string.gmatch(a,"[%S]+")do local e=vim.json.decode(d)if e~=nil then local f=e.line_num-1;local g=table.concat(e.corrections," or ")table.insert(c,{lnum=f,end_lnum=f,col=e.byte_offset,end_col=e.byte_offset+e.type:len(),severity=vim.diagnostic.severity.WARN,source="[typos] "..e.type,message="`"..e.typo.."` should be `"..g.."`"})end end;return c end}
