local a={}a.mean=function(b)local c=0;local d=0;for e,f in pairs(b)do if type(f)=="number"then c=c+f;d=d+1 end end;return c/d end;a.median=function(b)local g={}for e,f in pairs(b)do if type(f)=="number"then table.insert(g,f)end end;table.sort(g)if math.fmod(#g,2)==0 then return(g[#g/2]+g[#g/2+1])/2 else return g[math.ceil(#g/2)]end end;a.std_dev=function(b)local h,i,j;local c=0;local d=0;h=a.mean(b)for e,f in pairs(b)do if type(f)=="number"then i=f-h;c=c+i*i;d=d+1 end end;j=math.sqrt(c/(d-1))return j end;a.maxmin=function(b)local k=-math.huge;local l=math.huge;for e,f in pairs(b)do if type(f)=="number"then k=math.max(k,f)l=math.min(l,f)end end;return k,l end;return a
