local a={}function a.get_hl(b)local c,d=pcall(vim.api.nvim_get_hl_by_name,b,true)if not c then return end;for e,f in pairs({"foreground","background","special"})do if d[f]then d[f]=string.format("#%06x",d[f])end end;return d end;function a.hex2rgb(g)g=g:gsub("#","")return tonumber("0x"..g:sub(1,2)),tonumber("0x"..g:sub(3,4)),tonumber("0x"..g:sub(5,6))end;function a.rgb2hex(h,i,j)return string.format("#%02x%02x%02x",h,i,j)end;function a.is_dark(g)local h,i,j=a.hex2rgb(g)local k=(0.299*h+0.587*i+0.114*j)/255;return k<=0.5 end;function a.warn(l)vim.notify(l,vim.log.levels.WARN,{title="TodoComments"})end;function a.error(l)vim.notify(l,vim.log.levels.ERROR,{title="TodoComments"})end;return a
